import React, {useState} from 'react';
import './App.css';
import {useDispatch, useSelector} from "react-redux";
import {RootStore} from "./Store";
import {GetPokemon} from "./actions/PokemonActions";


function App() {
  const dispatch = useDispatch();
  const [pokemonName, setPokemonName] = useState("");
  const [todoList, setTodoList] = useState<DefaultStateI[]>([]);

  const pokemonState = useSelector((state: RootStore) => state.pokemon);
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => setPokemonName(event.target.value);
  const handleSubmit = () => dispatch(GetPokemon(pokemonName));
  console.log("pokemon state:", pokemonState);

  return (
  <div className="App">
      <input type="text" onChange={handleChange}/>
      <button onClick={handleSubmit}>Search</button>
      {pokemonState.pokemon && (            
    <table id="customers">
      <tr>
        <th>Gender: </th>
        <th>Country: </th>
        <th>Name: </th>
      </tr>
      <tr>
        <td>{pokemonState.pokemon.Gender}</td>
        <td>{pokemonState.pokemon.Country}</td>
        <td>{pokemonState.pokemon.Name}</td>
      </tr>
    </table>
      )}
  </div>
  );
}


export default App;
