import {
  Name_FAIL,
  NAME_LOADING,
  Name_SUCCESS,
  NameDispatchTypes,
  NameType
} from "../actions/PokemonActionTypes";

interface DefaultStateI {
  loading: boolean,
  pokemon?: NameType
}

const defaultState: DefaultStateI = {
  loading: false
};

const pokemonReducer = (state: DefaultStateI = defaultState, action: NameDispatchTypes) : DefaultStateI => {
  switch (action.type) {
    case Name_FAIL:
      return {
        loading: false,
      }
    case NAME_LOADING:
      return {
        loading: true,
      }
    case Name_SUCCESS:
      return {
        loading: false,
        pokemon: action.payload
      }
    default:
      return state
  }
};


export default pokemonReducer
