import {Dispatch} from "redux";
import {NAME_LOADING, POKEMON_LOADING, POKEMON_SUCCESS, PokemonDispatchTypes} from "./PokemonActionTypes";
import axios from "axios";

export const GetPokemon = (name: string) => async (dispatch: Dispatch<PokemonDispatchTypes>) => {
  try {
    dispatch({
      type: POKEMON_LOADING
    })

    const res = await axios.get(`http://localhost:3500/user/info/${name}`);

    dispatch({
      type: POKEMON_SUCCESS,
      payload: res.data
    })

  } catch(e) {
    dispatch({
      type: POKEMON_FAIL
    })
  }
};
