import {Dispatch} from "redux";
import {Name_FAIL, NAME_LOADING, Name_SUCCESS, NameDispatchTypes} from "./PokemonActionTypes";
import axios from "axios";

export const GetPokemon = (name: string) => async (dispatch: Dispatch<NameDispatchTypes>) => {
  try {
    dispatch({
      type: NAME_LOADING
    })

    const res = await axios.get(`http://localhost:3500/user/info/${name}`);

    dispatch({
      type: Name_SUCCESS,
      payload: res.data
    })

  } catch(e) {
    dispatch({
      type: Name_FAIL
    })
  }
};
